$(document).ready(function(){$("#add_new_button").on("click",function(e){e.preventDefault(),$("#add_user_modal").modal("show")}),$("#generate_code_button").on("click",function(e){e.preventDefault(),$("#generate_code_modal").modal("show")})});$(document).ready(function(){$("#submit-btn").on("click",function(){let e=!0;$("#register-form").find("input[required], select[required]").each(function(){$(this).val()===""?(e=!1,$(this).addClass("is-invalid")):$(this).removeClass("is-invalid")}),e?$("#register-form").submit():console.log("Please fill all the required fields.")}),$("#register-form").on("input change","input[required], select[required]",function(){$(this).val()===""?$(this).addClass("is-invalid"):$(this).removeClass("is-invalid")})});$(document).ready(function(){$("#generate_code_form").on("submit",function(e){e.preventDefault(),$.ajax({url:$(this).attr("action"),method:"POST",data:$(this).serialize(),success:function(i){i.code?$("#generated_code").html("<p>"+i.code+"</p>"):$("#generated_code").html("<p>Error generating code. Please try again.</p>")},error:function(){$("#generated_code").html("<p>Error generating code. Please try again.</p>")}})})});
